INSERT INTO `wp_term_taxonomy` (`term_taxonomy_id`, `term_id`, `taxonomy`, `description`, `parent`, `count`) VALUES
(1, 1, 'category', '<strong>「時代の変化を恐れない女性は、美しい」</strong>\r\n\r\n女性の生き方が画一的だったのは、もはや過去の話。\r\n\r\n世界に目を向ければ、いくつになっても人生を謳歌する老女や\r\nビジャブを脱ぐ中東の女性、新たな美意識を提案するコスメなど、\r\nステレオタイプでない事例がいくらでもある。\r\n\r\n世界で生まれ続ける新しい潮流、古い慣習や文化を覆す価値感、\r\nそれを体現する女性とその意識の周辺こそが\r\nTABI LABOの思うBEAUTYだ。\r\n', 0, 14),
(2, 2, 'category', '<strong>「つながった世界では、日常生活も大きく変わる」</strong>\r\n\r\nインターネットやLCCによって、\r\n情報伝達も人の移動も簡略化・スピード化した。\r\n\r\n世界がつながったことで、\r\nローカルはいとも簡単にグローバルになる。\r\n\r\n「Localization（地域） = Globalization（海外）」\r\n\r\nあらゆる地域のライフスタイルは、距離を超えて伝播するし、\r\n移動すること自体を生活とする人も増えている。\r\n\r\nTABI LABOは、世界の新しいライフスタイルを追っていきたい。\r\n', 0, 2),
(3, 3, 'category', '<strong>「素晴らしき世界の多様性」</strong>\r\n\r\n世界中にはさまざまな「違い」がある。\r\nそれは数字で測れる「差」ではない。\r\n\r\n「差」は、それを埋めたり追い越したりするものだ。\r\n一方「違い」は、競うことなく、尊重するものだ。\r\n\r\nそれがアートであれ、デザインであれ、性であれ、\r\nTABI LABOは世界の多様性をポジティブに捉えている。\r\n「違い」を大いに楽しみたい。\r\n', 0, 1),
(4, 4, 'category', '<strong>「ありふれた旅を脱却しよう」</strong>\r\n\r\n特別な経験をもたらす旅がある。\r\n\r\nあなただけのテーマを持って、新しいことに触れてみる。\r\nそれは冒険と言い換えてもいい。\r\n\r\n絶景を求めて辺境の地に行くこともそうだし、\r\nAirbnbで一期一会の宿泊体験をすることもそう、\r\n背伸びして超高級ホテルに泊まるのだって冒険だ。\r\n\r\n要は、あなただけのテーマを持って移動をすること。\r\nTABI LABOは、それこそがTRAVELだと考えている。\r\n', 0, 1),
(5, 5, 'category', '<strong>「つながった世界では、日常生活も大きく変わる」</strong>\r\n\r\nインターネットやLCCによって、\r\n情報伝達も人の移動も簡略化・スピード化した。\r\n\r\n世界がつながったことで、\r\nローカルはいとも簡単にグローバルになる。\r\n\r\n「Localization（地域） = Globalization（海外）」\r\n\r\nあらゆる地域のライフスタイルは、距離を超えて伝播するし、\r\n移動すること自体を生活とする人も増えている。\r\n\r\nTABI LABOは、世界の新しいライフスタイルを追っていきたい。\r\n', 0, 0),
(6, 6, 'category', '<strong>「探求し続ける者たち」</strong>\r\n\r\n歩みを止めない、真の探求者が世界にいる。\r\n\r\n開拓者／pioneer（パイオニア）\r\n革新者／innovator（イノベーター）\r\n先駆者、先導者／vanguard（ヴァンガード）\r\n\r\nじつは、呼び名はなんでもいい。\r\nTABI LABOは探求し続ける人が大好きだ。\r\n彼らは時代に敏感で、自らの殻を破ることを厭わない。\r\n\r\n「この世に生き残る生き物は、最も力の強いものか？　そうではない。最も頭のいいものか？　そうでもない。それは、変化に対応できる生き物だ」　\r\n-チャールズ・ダーウィン-\r\n', 0, 2),
(7, 7, 'category', '<strong>「世界の問題を知る、その責任がある」</strong>\r\n\r\n食料、環境、経済・貧困、戦争、そして社会。\r\nこれらのワードの下の句は「問題」だ。\r\n\r\n世界の多様性は、残念ながらこれらにも当てはまる。\r\nそして、すべての問題はつながっているかもしれない。\r\n\r\nもしかしたら、遠い海外の地で暮らす人の苦しみが\r\n自分たちの生活を支えているかもしれない・・・。\r\n\r\n解決への第一歩は、なによりもまず世界中の実態を知ることだ。\r\n', 0, 1),
(8, 8, 'category', '', 0, 0),
(9, 9, 'category', '<strong>「その姿は、感動と衝動を生む」</strong>\r\n\r\n一般的なスポーツの定義が「身体運動による競技」だとすれば、\r\nTABI LABOはそこに「冒険的な要素」をプラスしたい。\r\n\r\nそれは必ずしもエクストリームなものではない。\r\n既存の競技のなかにも、リフレッシュ目的の体操のなかにだって、\r\nチャレンジングなテクニックやマインドは確実にある。\r\n\r\nそれは時に観る者の心を揺さぶり、フィジカルな衝動を生む。\r\n', 0, 0),
(10, 10, 'category', '', 0, 0),
(11, 11, 'nav_menu', '', 0, 10),
(12, 12, 'category', '', 0, 1),
(13, 13, 'category', '', 0, 1),
(14, 14, 'category', '', 0, 1),
(15, 15, 'nav_menu', '', 0, 10),
(16, 16, 'category', '<strong>「自在に食べ、心豊かに味わう」</strong>\r\n\r\n気のあう仲間と美味しい料理。\r\n人生を豊かにするものにこのふたつは欠かせない。\r\n大好きな人とふたりだけで食べる時間もまたしかり。\r\n\r\n手間ひまかけずシンプルなレシピや\r\n誰かに教えたくなる、とっておきのカフェやレストラン。\r\n\r\n「食」を通じてTABI LABOは”しあわせ”を発信していきたい。\r\nなぜなら「美味しい！」は、しあわせに直結する共通言語だから。\r\n', 0, 0);
